async def parse_number(text):
    # –†–µ–≥—É–ª—è—Ä–Ω–æ–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ –¥–ª—è –ø–æ–∏—Å–∫–∞ —á–∏—Å–ª–∞ —Å –∫–∏–ª–æ-–ø–æ—Å—Ç—Ñ–∏–∫—Å–æ–º (1–∫, 20–∫ –∏ —Ç.–¥.)
    match = re.match(r'(\d+)([–∫–ökK]+)', text)
    if match:
        number = int(match.group(1))
        postfix = match.group(2)
        # –£–º–Ω–æ–∂–∞–µ–º –Ω–∞ 1000, –µ—Å–ª–∏ –µ—Å—Ç—å –∫–∏–ª–æ-–ø–æ—Å—Ç—Ñ–∏–∫—Å
        if postfix.lower() == '–∫':
            number *= 1000
        elif len(postfix) > 1:  # –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –±—É–∫–≤—ã "–∫"
            number *= 100 ** (len(postfix) - 1) * 10000
        return number
    # –ï—Å–ª–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º None
    return text

async def influence_lvl(point):
    next_level_points = [2500, 10000, 50000, 100000, 250000, 500000, 2000000, 3500000, 7000000]

    for i, points in enumerate(next_level_points):
        if point < points:
            return i + 1

    return 10  # If point is greater than or equal to 7000000, return level 10

