async def parse_number(text):
    # Регулярное выражение для поиска числа с кило-постфиксом (1к, 20к и т.д.)
    match = re.match(r'(\d+)([кКkK]+)', text)
    if match:
        number = int(match.group(1))
        postfix = match.group(2)
        # Умножаем на 1000, если есть кило-постфикс
        if postfix.lower() == 'к':
            number *= 1000
        elif len(postfix) > 1:  # Дополнительные буквы "к"
            number *= 100 ** (len(postfix) - 1) * 10000
        return number
    # Если не найдено, возвращаем None
    return text

async def influence_lvl(point):
    for i, points in enumerate(next_level_points):
        if point < points:
            return i + 1

    return 10  # If point is greater than or equal to 7000000, return level 10

